import React from 'react';
import './GerenciarProdutos.css';
import Pagination from '@mui/material/Pagination';
import CreateIcon from '@mui/icons-material/Create';
import DeleteIcon from '@mui/icons-material/Delete';
import LojaService from '../../services/LojaService';
import InputLabel from '@mui/material/InputLabel';
import Paper from '@mui/material/Paper';
import InputBase from '@mui/material/InputBase';
import Divider from '@mui/material/Divider';
import IconButton from '@mui/material/IconButton';
import SearchIcon from '@mui/icons-material/Search';
import AddIcon from '@mui/icons-material/Add';
import { useState, useEffect } from 'react';
import { useParams } from "react-router-dom";
import MapView from '../../maps';
import Modal from '@mui/material/Modal';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Toasts from '../../components/Toasts'
import ProdutoService from '../../services/ProdutoService';
import ProdutosLista from '../../components/ProdutosLista'
import { ToastContainer } from 'react-toastify';
const style = {
    position: 'absolute',
    top: '50%',
    left: '50%',
    transform: 'translate(-50%, -50%)',
    width: 400,
    bgcolor: 'background.paper',
    border: '2px solid #000',
    boxShadow: 24,
    p: 4,

};


function GerenciarProdutos() {
    //nome da loja
    const { nome } = useParams();
    const { id } = useParams();
    //Trazendo os produtos e a loja
    const [produtosLista, setProdutos] = useState([]);
    const [lojaDetalhe, setLoja] = useState([]);
    const [pesquisa, setPesquisa] = useState('');
    const [page, setPage] = useState(1);
    //puxando o primeiro array
    // const lojaDetalhada = lojaDetalhe



    //logica de imagem
    const imageDefault = "data:image/png;base64,";
    const imageCard = lojaDetalhe.imagem === null || lojaDetalhe.imagem == '' || lojaDetalhe.imagem === undefined ? imageDefault : lojaDetalhe.imagem;

    //coordenadas para o maps
    const latitude = lojaDetalhe.latitude;
    const longitude = lojaDetalhe.longitude;




    //dados do produto
    const [nomeProduto, setNome] = useState('')
    const [descricao, setDescricao] = useState('')
    const [recompensa, setRecompensa] = useState('')
    const [preco, setPreco] = useState('')
    const [imagem, setImagem] = useState()

    //logicas para o modal
    const [open, setOpen] = useState(false);
    const handleOpen = () => setOpen(true);
    const handleClose = () => setOpen(false);
    const [checked, setChecked] = useState(false);
    const [isDisabled, setIsDisabled] = useState(true);
    const [checkedTwo, setCheckedTwo] = useState(false);
    const [isDisabledTwo, setIsDisabledTwo] = useState(true);

    //metodo para pegar valor da page
    const handleChangePage = (event, value) => {
        setPage(value);
    };
    const addNome = event => {
        setNome(event.target.value);
    };
    const addImagem = event => {

        const file = event.target.files[0].file;
        setImagem(file);
    };
    const addDescricao = event => {
        setDescricao(event.target.value);
    };
    const addPreco = event => {
        setPreco(event.target.value);
    };
    const addRecompensa = event => {
        setRecompensa(event.target.value);
    };

    const limparCampos = () => {
        setNome('')
        setDescricao('')
        setPreco('')
        setRecompensa('')
        setImagem('')
        handleChange()
        handleChangeTwo()
    }
    const handleChange = () => {

        setChecked(!checked)
        if (checked === false) {
            setChecked(true)
            setIsDisabled(!isDisabled)

            console.log("Clicando" + checked)
        } else {
            setIsDisabled(!isDisabled)

        }

    };

    const handleChangeTwo = () => {

        setCheckedTwo(!checkedTwo)
        if (checkedTwo === false) {
            setCheckedTwo(true)
            setIsDisabledTwo(!isDisabledTwo)

            //console.log("Clicando" + checkedTwo)
        } else {
            setIsDisabledTwo(!isDisabledTwo)

        }

    };
    const dadosProduto = {
        nome: nomeProduto,
        descricao: descricao,
        precoTcoins: preco,
        valorRecompensa: recompensa,
        imagem: imagem
    }
    const produtosListaInverso = [...produtosLista].reverse()




    //pegar os dados por página
    useEffect(() => {
        getLojaDetalhe();
        getProdutos();
    }, [page])

    useEffect(() => {
        setPage(1)
        getProdutos();
    }, [produtosLista, pesquisa])
    // const childToParent = (childdata) => {
    //     if (childdata) {
    //         console.log('atualiza')
    //         getProdutos()
    //     }
    // }
    //onde tiver 1 trocar por produto id
    async function createProdut() {
        const produtoAtualizado = await ProdutoService.createProduto(id, dadosProduto);
        if (produtoAtualizado.status == 200 || produtoAtualizado.status == 404) {
            handleClose()
            Toasts.sucesso('Produto cadastrado com sucesso!')
            limparCampos()

        } else if (produtoAtualizado.status == 500) {
            console.log('erro ao salvar')
            Toasts.erro('Erro ao cadastrar produto!')
        };

    }



    //reformular pra loja/info
    async function getLojaDetalhe() {
        const loja = await LojaService.getLoja(id);
        if (loja.status == 200 || loja.status == 404) setLoja(loja.data);

    }
    async function getProdutosPorPagina() {
        const produtos = await ProdutoService.getProdutosByLoja('', '', page, 4);
        if (produtos.status == 200 || produtos.status == 404) setProdutos(produtos.data);

    }

    async function getProdutos() {
        const produtos = await ProdutoService.getProdutosByLoja(pesquisa, id, '', 4);
        if (produtos.status == 200 || produtos.status == 404) setProdutos(produtos.data);
    }


    return (

        <div className='Container'>
            <div className='Nav'>
                <div>
                    <div className='Bloco1'>
                        <div className='ImagemLoja'>
                            <img className='ImagemLoja' src={imageCard} alt="Imagem da Loja"></img>
                        </div>
                        <div className='NomeLoja'>
                            <h1>{nome}</h1>
                            <p>{lojaDetalhe.ramo}</p>
                        </div>
                    </div>
                    <p className='DescricaoLoja'>{lojaDetalhe.descricao}</p>
                </div>
                <div className='Localizacao map'>
                    <MapView lat={latitude} lng={longitude}></MapView>
                </div>
            </div>

            <Paper
                className="BarraPesquisa"
                component="form"
                sx={{ p: '2px 4px', display: 'flex', alignItems: 'center' }}
            >
                <InputBase
                    sx={{ ml: 1, flex: 1 }}
                    placeholder="Buscar por nome"
                    inputProps={{ 'aria-label': 'Buscar por nome' }}
                    onChange={e => setPesquisa(e.target.value.toLowerCase())}
                />
                <IconButton type="submit" sx={{ p: '10px' }} aria-label="search">
                    <SearchIcon />
                </IconButton>
                <Divider sx={{ height: 28, m: 0.5 }} orientation="vertical" />
                <IconButton color="primary" sx={{ p: '10px' }} aria-label="add" onClick={handleOpen}>
                    <AddIcon />
                </IconButton>
            </Paper>



            <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
            >
                <Box sx={style}>
                    <Typography id="modal-modal-title" variant="h6" component="h2" sx={{ textAlign: 'center' }}>
                        Adicionar Produto
                    </Typography>
                    <TextField sx={{ marginTop: 2, marginBottom: 1 }}
                        autoComplete="given-name"
                        name="Nome"
                        required
                        value={nomeProduto}
                        fullWidth
                        id="firstName"
                        onChange={addNome}
                        label="Nome do produto"
                        autoFocus />

                    <InputLabel id="demo-simple-select-label">Foto</InputLabel>
                    <TextField sx={{ marginBottom: 1 }}

                        type="file"
                        fullWidth
                        value={imagem}
                        onClick={addImagem}
                        required
                        id="Foto"

                    />
                    <TextField
                        sx={{ height: 8, marginBottom: 5 }}
                        autoComplete="given-name"
                        name="Descricao"
                        value={descricao}
                        onChange={addDescricao}
                        required
                        fullWidth
                        id="Descricao"
                        label="Descrição" />
                    <fieldset className='Fieldset'>
                        <legend className='Legenda'>Recompensa em tcoins(TC)</legend>
                        <label className="switch">
                            <input type="checkbox" onClick={handleChange} checked={checked}
                            ></input>

                            <span className="slider round"></span>
                        </label>
                        <input type="number" className="InputNumber" placeholder='Valor da recompensa' value={recompensa} onChange={addRecompensa} disabled={isDisabled}></input>
                    </fieldset>
                    <fieldset className='Fieldset'>
                        <legend className='Legenda'>Comprar com tcoins(TC)</legend>
                        <label className="switch">
                            <input type="checkbox" onClick={handleChangeTwo} checked={checkedTwo}></input>
                            <span className="slider round"></span>
                        </label>
                        <input type="number" className="InputNumber" placeholder='Valor da compra' value={preco} onChange={addPreco} disabled={isDisabledTwo}></input>
                    </fieldset>

                    <Button
                        sx={{ marginBottom: 1, backgroundColor: 'red', marginTop: 2 }}
                        type="submit"
                        fullWidth
                        variant="contained"
                        onClick={handleClose}
                    >
                        CANCELAR
                    </Button>
                    <Button
                        sx={{ backgroundColor: 'green' }}
                        type="submit"
                        fullWidth
                        variant="contained"
                        onClick={createProdut}
                    >
                        SALVAR
                    </Button>
                </Box>
            </Modal>
            <ToastContainer
                position="top-right"
                autoClose={5000}
                hideProgressBar={false}
                newestOnTop={false}
                closeOnClick
                rtl={false}
                pauseOnFocusLoss
                draggable
                pauseOnHover
                theme='colored'
            />


            {produtosListaInverso.map((produto, i) => (
                <ProdutosLista produto={produto} lojaId={id}></ProdutosLista>

            ))}
            <div className='Paginacao'>
                <Pagination color="secondary" count={10} page={page} onChange={handleChangePage} />
            </div>

        </div>

    )
}

export default GerenciarProdutos;